showSelectFiles = function showSelectFiles(_event){

	filelistTag = ''
	filelist = _event.target.files;
	for(i = 0; i < filelist.length; ++i){
		//filelistTag += "<li>"+filelist[i].name+"</li>";
		filelistTag += "<li> $1 </li>".format(filelist[i].name);
	}
	alert(filelistTag);
}
